inline bool check(int a, int x, int y, int z)
{
	int u = 0, v = 0, w = 0;
	#define pows(a, b) \
		u = (a), v = (b), w = 1;\
		while(v) { if(v & 1) w = w * u % x; u = u * u % x, v >>= 1; }
	pows(a, z);
	if(w > 1) { 
		for(int i = 0; i < y; i++)
		{
			if(w == x - 1) return true;
			w = w * w % x;
		}
		return false;
	}
	else return true;
}
bool isprime(int x)
{
	if(x == 2 || x == 3 || x == 7 || x == 61) return true;
	if(!(x % 2 && x % 7 && x % 61 && x % 3) || x == 1) return false;
	int y = 0, z = x - 1; while(!(z & 1)) y++, z >>= 1;
	bool ok = true;	
	ok &= check(2, x, y, z);
	ok &= check(3, x, y, z);
	ok &= check(7, x, y, z);
	ok &= check(61, x, y, z);
	return ok;
}
