#include <bits/stdc++.h>

typedef std::pair <int, int> pr;
typedef std::map <int, int> map;
const int N = 108;

int n, O, E;
int o[N], e[N];
map S;
int pn = 0, c[10000054], p[666666];

void sieve(int n) {
	int i, j, v;
	memset(c, -1, sizeof c);
	for (i = 2; i <= n; ++i) {
		if (!~c[i]) p[pn] = i, c[i] = pn++;
		for (j = 0; (v = i * p[j]) <= n && j <= c[i]; ++j) c[v] = j;
	}
}

inline bool is_prime(int x) {return p[c[x]] == x;}

namespace CF {
	#define ad(x) ((x - 1 ^ 1) + 1)

	const int N = 2000, M = 100000, INF = 0x7f7f7f7f;

	struct edge {
		int u, v, c, f;
		edge (int u0 = 0, int v0 = 0, int c0 = 0, int f0 = 0) : u(u0), v(v0), c(c0), f(f0) {}
	} e[M];

	int V = 2, E = 0, si = 1, ti = 2, flow, cost;
	int first[N], next[M];
	int dep[N], cur[N], que[M << 1];
	bool in_que[N], used[N];

	inline void addedge(int u, int v, int c, int f) {
		e[++E] = edge(u, v, c, f), next[E] = first[u], first[u] = E;
		e[++E] = edge(v, u, -c), next[E] = first[v], first[v] = E;
	}

	bool bfs() {
		int h = M, t = h + 1, i, x, y;
		memset(dep, 127, sizeof dep);
		que[h] = ti, dep[ti] = 0, in_que[ti] = true;
		for (; h < t; ) {
			x = que[h++], in_que[x] = false;
			for (i = first[x]; i; i = next[i])
				if (dep[y = e[i].v] > dep[x] - e[i].c && e[ad(i)].f) {
					dep[y] = dep[x] - e[i].c;
					if (!in_que[y])
						in_que[y] = true, (dep[y] >= dep[que[h]] ? que[t++] : que[--h]) = y;
				}
		}
		return dep[si] < INF;
	}

	int dfs(int x, int lim) {
		int a, c, f = 0;
		if (x == ti || !lim) return lim;
		used[x] = true;
		for (int &i = cur[x]; i; i = next[i])
			if (dep[e[i].v] == dep[x] - e[i].c && e[i].f && !used[e[i].v]) {
				a = std::min(lim - f, e[i].f);
				c = dfs(e[i].v, a);
				e[i].f -= c, e[ad(i)].f += c;
				if ((f += c) == lim) return f;
			}
		return f;
	}

	int Dinic() {
		int f;
		for (cost = flow = 0; bfs(); ) {
			memcpy(cur, first, sizeof cur);
			memset(used, 0, sizeof used);
			flow += f = dfs(si, INF);
			cost += dep[si] * f;
		}
		return cost;
	}
}

int main() {
	int i, j, x;
	scanf("%d", &n), sieve(10000053);
	for (i = 0; i < n; ++i) scanf("%d", &x), ++S[x], ++S[x + 1];
	for (const pr &p : S) if (p.second & 1) (p.first & 1 ? o[O++] : e[E++]) = p.first;
	for (i = 0; i < O; ++i) CF::addedge(1, 3 + i, 0, 1);
	for (j = 0; j < E; ++j) CF::addedge(3 + O + j, 2, 0, 1);
	for (i = 0; i < O; ++i)
		for (j = 0; j < E; ++j)
			CF::addedge(3 + i, 3 + O + j, is_prime(abs(o[i] - e[j])) ? 1 : 3, 1);
	printf("%d\n", CF::Dinic() + abs(O - E));
	return 0;
}

