#include <bits/stdc++.h>
using namespace std;
// flow from KACTL
typedef long long LL;
struct Flow{
	int n;
	vector<vector<int> > graph; //list of id's
	vector<int> st, en, back; //back = back edge id
	vector<LL> f, c;
	vector<int> par;
	Flow(int n): n(n), graph(n), par(n){}
	void add_edge(int u, int v, int cap){
		int id1 = st.size();
		int id2 = id1 + 1;
		st.push_back(u); st.push_back(v);
		en.push_back(v); en.push_back(u);
		back.push_back(id2); back.push_back(id1);
		f.push_back(0); f.push_back(0);
		c.push_back(cap); c.push_back(0);
		graph[u].push_back(id1);
		graph[v].push_back(id2);
	}

	void add(int id1, int v){
		f[id1] += v;
		c[id1] -= v;
		f[back[id1]] -= v;
		c[back[id1]] += v;
	}

	LL maxflow(int s, int t){
		LL ans = 0;
		vector<int> bfs;
		if(s == t) return ans;
		while(1){
			for(int i = 0; i < n; i++) par[i] = -1;
			bfs.clear();
			bfs.push_back(s); par[s] = -2;
			int cur = 0;
			while(cur < bfs.size()){
				int u = bfs[cur]; cur++;
				for(int i = 0; i < graph[u].size(); i++){
					int id = graph[u][i];
					if(c[id] == 0) continue;
					if(par[en[id]] != -1) continue;
					par[en[id]] = id;
					bfs.push_back(en[id]);
				}
			}
			if(par[t] == -1) break;
			LL send = 4e18;
			int curv = t;
			while(par[curv] != -2){
				send = min(send, c[par[curv]]);
				curv = st[par[curv]];
			}
			curv = t;
			while(par[curv] != -2){
				add(par[curv], send);
				curv = st[par[curv]];
			}
			ans += send;
		}
		return ans;
	}
};
int main(){
	int n;
	cin >> n;
	int z[n];
	map<int,int> f;
	for(int i = 0; i < n; i++){
		cin >> z[i];
		f[z[i]]++;
		f[z[i]+1]++;
	}
	vector<int> r;
	vector<int> odd, even;
	for(map<int,int>::iterator it = f.begin(); it != f.end(); it++){
		if(it->second == 1){
			r.push_back(it->first);
			if(it->first % 2){
				odd.push_back(it->first);
			} else {
				even.push_back(it->first);
			}
		}
	}
	Flow s(300);
