bool isPrime(int x) {
    if (x == 1) return false;

    for (int i = 2; i * i <= x; i++) if (x % i == 0) {
        return false;
    }

    return true;
}


struct TurboMatching {
    int value = 0;

    VVI G;
    VI mate; VB vis;

    TurboMatching(int n): G(n), mate(n,-1), vis(n,0) {}

    void addEdge(int u, int v) {
        G[u].pb(v);
        G[v].pb(u);
    }

    bool findPath(int v) {
        if (vis[v]) return false;
        vis[v] = true;

        for (int u : G[v]) if (mate[u] == -1 || findPath(mate[u])) {
            mate[v] = u;
            mate[u] = v;
            return true;
        }

        return false;
    }

    pair <int,VI> runMatching() {
        while (true) {
            bool found = false;
            FORE(v,G) vis[v] = false;

            FORE(v,G) if (mate[v] == -1 && findPath(v)) {
                found = true;
                value++;
            }

            if (!found) break;
        }

        return {value, mate};
    }
};

/*************************************************************************/

int solve(int n, VI &x) {
    TurboMatching matching(n);

    FOR(i,0,n-1) FOR(j,i+1,n-1) {
        int diff = abs(x[i] - x[j]);

        if (diff % 2 && isPrime(diff)) {
            matching.addEdge(i, j);
        }
    }

    int matchingInOnes = matching.runMatching().st;

    int odd = 0, even = 0;
    FOR(i,0,n-1) {
        if (x[i] % 2) odd++;
        else even++;
    }

    int ans = matchingInOnes;
    ans += 2 * (odd / 2);
    ans += 2 * (even / 2);

    if (odd % 2) {
        ans += 3;
    }

    return ans;
}

