	edge(int a, int b, int c){
		to = a, cap = b, rev = c;
	}
};

int n, N;
int x[105], X[205];
bool prime[10000005];
map<int, int> mp;
vector<int> vec;

vector<edge> G[105];
int S, T;
bool used[105];

int dfs(int v, int f)
{
	used[v] = true;
	if(v == T) return f;

	int ret;
	for(int i = 0; i < G[v].size(); i++){
		if(used[G[v][i].to] || G[v][i].cap <= 0) continue;
		ret = dfs(G[v][i].to, min(f, G[v][i].cap));
		if(ret > 0){
			G[v][i].cap -= ret;
			G[G[v][i].to][G[v][i].rev].cap += ret;
			return ret;
		}
	}
	return 0;
}

void add_edge(int s, int t, int cap)
{
	G[s].push_back(edge(t, cap, G[t].size()));
	G[t].push_back(edge(s, 0, G[s].size()-1));
}

int main(void)
{
	cin >> n;
	for(int i = 2; i < 10005; i++){
		if(prime[i]) continue;
		for(int j = 2*i; j < 10000005; j+=i) prime[j] = true;
	}
	prime[1] = prime[2] = true;

	for(int i = 1; i <= n; i++) cin >> x[i];
	for(int i = 1; i <= n; i++){
		mp[x[i]]++, mp[x[i]+1]++;
	}
	for(auto it = mp.begin(); it != mp.end(); it++){
		if(it->second % 2) vec.push_back(it->first);
	}
	N = vec.size();
	for(int i = 0; i < N; i++) X[i+1] = vec[i];

	S = N+1, T = N+2;
	int odd = 0, even = 0;
	for(int i = 1; i <= N; i++){
		if(X[i]%2) add_edge(S, i, 1), odd++;
		else add_edge(i, T, 1), even++;
	}
	for(int i = 1; i <= N; i++){
		for(int j = 1; j <= N; j++){
			if(i == j) continue;
			if(X[i]%2 && X[j]%2 == 0 && !prime[abs(X[i]-X[j])]) add_edge(i, j, 1);
		}
	}

	int mx = 0, flow;
	while(1){
		for(int i = 1; i <= T; i++) used[i] = false;
		flow = dfs(S, inf);
		if(flow <= 0) break;
		mx += flow;
	}

	int ans = mx;
	odd -= mx, even -= mx;
	ans += odd/2*2, ans += even/2*2;
	ans += odd%2*3;

	cout << ans << endl;
	return 0;
}

