class F {
public:
	Sieve S{10000000};
	vector<int> X;

	int cost(int x) {
		if (x == 0) return 0;
		if (x%2==0) return 2;
		if (S[x]) return 1;
		return 3;
	}

	void solve(istream& cin, ostream& cout) {
		int N; cin >> N;
		X.resize(N);
		cin >> X;

		// auto P = S.primes();

		vector2<int> D(N, N, 10000);
		vector2<int> J(N, N, 10000);
		for (int l = 0; l < N; ++l) {
 			for (int i = 0; i + l < N; ++i) {

				int j = i + l;
				if (l == 0) {
					D[i][j] = 3;
					J[i][j] = 0;
				}
				for (int k = i; k < j; ++k) {
					J[i][j] = min(J[i][j], J[i][k] + J[k+1][j] + cost(X[k+1] - X[k] - 1));
					D[i][j] = min(D[i][j], D[i][k] + D[k+1][j]);
				}

				D[i][j] = min(D[i][j], J[i][j] + cost(X[j] - X[i] + 1));
				J[i][j] = min(J[i][j], D[i][j] + cost(X[j] - X[i] + 1));

			}
		}

