            dep[e[t].r]=dep[x]+1,q[qr++]=e[t].r;
    }

    return dep[T]!=0;
}
int find(int u,int flow){
    if(u==T)return flow;
    int a=0,used=0;
    for(int& t=cur[u];t;t=e[t].nxt)if(e[t].w&&dep[e[t].r]==dep[u]+1&&(a=find(e[t].r,min(flow-used,e[t].w)))){
        e[t].w-=a;e[t^1].w+=a,used+=a;
        if(used==flow)return used;

    }
    if(!used)dep[u]=0;
    return used;
}
