//  Xu
bool dfs(int u) {
    for (int v : g[u]) {
        //  vvv
        if (vis[v] || lnk[v] == u) continue;
        //  vv
        vis[v] = 1;
        //  vv
        if (lnk[v] && !dfs(lnk[v])) continue;
        //  vlnk[v]vu
        lnk[v] = u; return true;
    }
    //  
