//  :
//  int nl: X; X[1...nl]
//  int nr: Y; Y[1...nr]
//  const vector<pii>& es: piifirstXsecondY
//  vector<pii> res: 
int match(int nl, int nr, const vector<pii>& es) {
    //  
    for (int i = 1; i <= nl; ++i)
        g[i].clear();
    fill_n(lnk + 1, nr, 0);
    for (pii e : es)
        g[e.first].push_back(e.second);
    //  
    for (int i = 1; i <= nl; ++i) {
        fill_n(vis + 1, nr, false);
        dfs(i);
    }
    //  Yilnk[i]
    int res = 0, lx = nl, ly = nr;
    for (int i = 1; i <= nr; ++i)
        if (lnk[i]) res++, lx--, ly--;
    res += lx / 2 * 2; lx -= lx / 2 * 2;
    res += ly / 2 * 2; ly -= ly / 2 * 2;
    assert((lx ^ ly) == 0);
    if (lx && ly) res += 3;
    return res;
}

