#define Int register int

using namespace std;

const int MAXN = 100, MAXV = 1e7, MAXM = 5e4, MAXP = 664579, INF = 0x3f3f3f3f;
int n, L, S, T, d[MAXV + 5];
int pn, Prime[MAXP + 5];
int CntL, CntR, Left[MAXM + 5], Right[MAXM + 5];
int Cnte = 1, Head[MAXM + 5];
bool Vis[MAXV + 5];
pair<int, int> Path[MAXM + 5];

struct Edge { int To, Flow, nxt; } Graph[MAXM * 2 + 5];

inline int Abs ( const int x ) { return x < 0 ? ~ x + 1 : x; }

inline int Min ( const int a, const int b ) { return a < b ? a : b; }

inline void Link ( const int s, const int t, const int f ) { Graph[++ Cnte] = { t, f, Head[s] }, Head[s] = Cnte; }

inline void AddEdge ( const int s, const int t, const int f ) {
#ifdef __DEBUG
	printf ( "<%d, %d, %d>\n", s, t, f );
#endif
	Link ( s, t, f ), Link ( f, t, 0 );
}

inline void Sieve ( const int n ) {
	Vis[0] = Vis[1] = true;
	for ( Int i = 2; i <= n; ++ i ) {
		if ( ! Vis[i] ) Prime[++ pn] = i;
		for ( Int j = 1; j <= pn && i * Prime[j] <= n; ++ j ) {
			Vis[i * Prime[j]] = true;
			if ( ! ( i % Prime[j] ) ) break;
		}
	}
}

inline bool BFS () {
	static queue<int> Q;
	for ( ; ! Q.empty (); Q.pop () );
	for ( Int i = 1; i <= T; ++ i ) Vis[i] = false;
	while ( ! Q.empty () ) {
		int u = Q.front (); Q.pop ();
		for ( Int i = Head[u], v; i; i = Graph[i].nxt ) {
			if ( ! Vis[v = Graph[i].To] && Graph[i].Flow > 0 ) {
				Q.push ( v ), Vis[v] = true, Path[v] = { u, i };
				if ( v == T ) return true;
			}
		}
	}
	return false;
}

inline int EK () {
	int ret = 0;
	for ( ; BFS (); ) {
		int MinFlow = INF;
		for ( Int u = T; u ^ S; u = Path[u].first ) MinFlow = Min ( MinFlow, Graph[Path[u].second].Flow );
		for ( Int u = T; u ^ S; u = Path[u].first ) {
			Graph[Path[u].second].Flow -= MinFlow;
			Graph[Path[u].second ^ 1].Flow += MinFlow;
		}
		ret += MinFlow;
	}
	return ret;
}

inline void Work () {
	Sieve ( MAXV );
	scanf ( "%d", &n );
	for ( Int i = 1, x; i <= n; ++ i ) {
		scanf ( "%d", &x ), ++ d[x];
		L = L > x ? L : x;
	}
	for ( Int i = 1; i <= L + 1; ++ i ) {
		if ( d[i] ^ d[i - 1] ) {
			if ( i & 1 ) {
				Left[++ CntL] = i;
			} else {
				Right[++ CntR] = i;
			}
		}
	}
	S = CntL + CntR + 1, T = S + 1;
	for ( Int i = 1; i <= CntL; ++ i ) AddEdge ( S, i, 1 );
	for ( Int i = 1; i <= CntR; ++ i ) AddEdge ( i + CntL, T, 1 );
	for ( Int i = 1; i <= CntL; ++ i ) {
		for ( Int j = 1; j <= CntR; ++ j ) {
			if ( ! Vis[Abs ( Left[i] - Right[j] )] ) {
				AddEdge ( i, j + CntL, 1 );
			}
		}
	}
	int mxf = EK (), Ans = mxf;
	Ans += ( CntL - mxf >> 1 << 1 ) + ( CntR - mxf >> 1 << 1 );
	Ans += ( ( CntL - mxf ) & 1 ) * 3;
	printf ( "%d\n", Ans );
}

int main () {
	Work ();
	return 0;
}

