    if(v==t){return f;}
    check[v]=1;
    for(int i=0;i<G[v].size();i++){
        edge &e = G[v][i];
        if(check[e.to]==0 && e.cap > 0){
            int ff =dfs(e.to,t,min(f,e.cap));
            if(ff>0){
                e.cap -= ff;
                G[e.to][e.rev].cap += ff;
                return ff;
            }
        }
    }
    return 0;
}

