                continue;
            }
            dist[e.to] = dist[v] + 1;
            if (e.to == tv) {
                return true;
            }
            que.push_back(e.to);
        }
    }

    return false;
}

int dfs(int v, int r) {
    for (int& i = ptr[v]; i < int(g[v].size()); i++) {
        auto& e = g[v][i];
        if (dist[e.to] <= dist[v] || e.cap == 0) {
            continue;
        }
        int d = dfs(e.to, min(r, e.cap));
        if (d == 0) {
            continue;
        }
        e.cap -= d;
        g[e.to][e.rev].cap += d;
        return d;
    }
    return 0;
}

int exec() {
    int res = 0;
    while (augment()) {
        fill_n(ptr, nv, 0);
        while (true) {
            int d = dfs(sv, INF);
            if (d == 0) {
                break;
            }
            res += d;
        }
    }
    return res;
}

