int match[205],k,ans;
vector <int> daftar,adj[205],prime;
bitset<205> visited;
void sieve(){
	bool isprime[batasprima+5];
	memset(isprime,true,sizeof(isprime));
	for(int i=2;i<=batasprima;i++)
	{
		if(!isprime[i])
			continue;
		prime.pb(i);
		for(int j=i+i;j<=batasprima;j+=i)
			isprime[j]=false;
	}
}
bool isprime(int angka){
	if(angka<=2)
		return false;
	for(auto isi:prime)
	{
		if(angka%isi==0)
			return false;
	}
	return true;
}
bool matching(int pos){
	if(visited[pos])
		return false;
	visited[pos]=true;
	for(auto isi:adj[pos])
	{
		if(match[isi]==-1||matching(match[isi]))
		{
			match[isi]=pos;
			match[pos]=isi;
			return true;
		}
	}
	return false;
}
void buatgraph(){	
	for(int i=0;i<daftar.size();i++)
	{
		for(int j=0;j<daftar.size();j++)
		{
			if(isprime(abs(daftar[i]-daftar[j])))
				adj[i].pb(j);
		}
	}
}
void hitungk(){
	k=0;
	memset(match,-1,sizeof(match));
	for(int i=0;i<daftar.size();i++)
	{
		if(match[i]!=-1)
			continue;
		visited.reset();
		if(matching(i))
			k++;
	}
}
void hitungans(){
	int genap=0,ganjil=0;
	for(auto isi:daftar)
	{
		if(isi&1)
			ganjil++;
		else
			genap++;
	}
	assert(ganjil>=k&&genap>=k);
	assert(ganjil%2==genap%2);
	ans=k+((ganjil-k)/2+(genap-k)/2)*2+((ganjil-k)%2)*3;
}
void isidaftar(){
	int n,tadi=0;
	scan(n);
	for(int i=0;i<n;i++)
	{
		int input;
		scan(input);
		assert(tadi<input);
		tadi=input;
		if(daftar.size()&&daftar.back()==input)
			daftar[daftar.size()-1]=input+1;			//nanti mau sa coba
		else
			daftar.pb(input),daftar.pb(input+1);
	}
	assert(daftar.size()<=200);
}
int main()
{
	sieve();
	isidaftar();
	buatgraph();
	hitungk();
	hitungans();
	cout<<ans<<endl;
}

