bool up[MAXX];

struct node{int he,iter,dis;}d[MAXN];
struct line{int to,nex,cap;}ed[MAXN*MAXN];

inline bool is_odd_prm(int x){
	if(x==2) return false;
	for(int i=3;i*i<=x;i+=2){
		if(x%i==0) return false;
	}
	return true;
}

inline void addE(int u,int v,int cap){
	static int cnt=1;
	ed[++cnt]=(line){v,d[u].he};
	d[u].he=cnt;
}

inline int revE(int i){return i^1;}

inline bool BFS(int s,int t,int n){
	for(int i=1;i<=n;++i)
		d[i].dis=-1;
	static queue<int> q;
	d[s].dis=0;
	q.push(s);
	int u;
	while(q.size()){
		u=q.front();
		q.pop();
		for(int i=d[u].he,v;i;i=ed[i].nex){
			if(ed[i].cap==0) continue;
			v=ed[i].to;
			if(d[v].dis==-1){
				d[v].dis=d[u].dis+1;
				q.push(v);
			}
		}
	}
	return d[t].dis!=-1;
}

int aug(int u,int rest,const int t){
	if(u==t) return rest;
	int ret=0;
	for(int &i=d[u].iter,v,cap,flow;i;i=ed[i].nex){
		v=ed[i].to,cap=ed[i].cap;
		if(d[v].dis!=d[u].dis+1 || cap==0)
			continue;
		flow=aug(v,min(cap,rest),t);
		ed[i].cap-=flow,ed[revE(i)].cap+=flow;
		ret+=flow;
		if(ret==rest) return ret;
	}
	if(ret==0) d[u].dis=-1;
	return ret;
}

inline int Dinic(int s,int t,int n){
	int ret=0;
	while(BFS(s,t,n)){
		for(int i=1;i<=n;++i)
			d[i].iter=d[i].he;
		ret+=aug(s,INT_MAX,t);
	}
	return ret;
}

