	for(int i=1;i<=n;++i){
		scanf("%d",&x);
		up[x]=true;
	}
	for(int i=1;i<=x+1;++i){
		if(up[i]!=up[i-1])
			i&1 ? x_id[++cnt_x]=i:y_id[++cnt_y]=i;
	}
	for(int i=1;i<=cnt_x;++i){
		for(int j=1;j<=cnt_y;++j){
			if(is_odd_prm(abs(x_id[i]-y_id[j])))
				addE(i,j+cnt_x,INT_MAX),addE(j+cnt_x,i,0);
		}
	}
	int k=Dinic((cnt_x+cnt_y)|1,cnt_x+cnt_y+2,cnt_x+cnt_y+2);
	printf("%d",k+((((cnt_x-k)>>1)+((cnt_y-k)>>1))<<1)+(cnt_x-k)%2*3);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;
// flow from KACTL
typedef long long LL;
#define rep(i, a, b) for(int i = a; i < (b); ++i)
#define trav(a, x) for(auto& a : x)
#define all(x) x.begin(), x.end()
#define sz(x) (int)(x).size()
typedef long long ll;
typedef pair<int, int> pii;
typedef vector<int> vi;
typedef ll Flow;

struct Edge {
	int dest, back;
	Flow f, c;
};
typedef vector<vector<Edge> > graph;

struct PushRelabel {
	graph g;
	vector<Flow> ec;
	vector<Edge*> cur;
	vector<vi> hs; vi H;
	PushRelabel(int n) : g(n), ec(n), cur(n), hs(2*n), H(n) {}

	void add_edge(int s, int t, Flow cap, Flow rcap=0) {
		if (s == t) return;
		Edge a = {t, sz(g[t]), 0, cap};
		Edge b = {s, sz(g[s]), 0, rcap};
		g[s].push_back(a);
		g[t].push_back(b);
	}

	void add_flow(Edge& e, Flow f) {
		Edge &back = g[e.dest][e.back];
		if (!ec[e.dest] && f) hs[H[e.dest]].push_back(e.dest);
		e.f += f; e.c -= f; ec[e.dest] += f;
		back.f -= f; back.c += f; ec[back.dest] -= f;
	}
	Flow maxflow(int s, int t) {
		int v = sz(g); H[s] = v; ec[t] = 1;
		vi co(2*v); co[0] = v-1;
		rep(i,0,v) cur[i] = g[i].data();
		trav(e, g[s]) add_flow(e, e.c);

		for (int hi = 0;;) {
			while (hs[hi].empty()) if (!hi--) return -ec[s];
			int u = hs[hi].back(); hs[hi].pop_back();
			while (ec[u] > 0)  // discharge u
				if (cur[u] == g[u].data() + sz(g[u])) {
					H[u] = 1e9;
					trav(e, g[u]) if (e.c && H[u] > H[e.dest]+1)
						H[u] = H[e.dest]+1, cur[u] = &e;
					if (++co[H[u]], !--co[hi] && hi < v)
						rep(i,0,v) if (hi < H[i] && H[i] < v)
							--co[H[i]], H[i] = v + 1;
					hi = H[u];
				} else if (cur[u]->c && H[u] == H[cur[u]->dest]+1)
					add_flow(*cur[u], min(ec[u], cur[u]->c));
				else ++cur[u];
		}
	}
};
int main(){
	int n;
	cin >> n;
	int z[n];
	map<int,int> f;
	for(int i = 0; i < n; i++){
		cin >> z[i];
		f[z[i]]++;
		f[z[i]+1]++;
	}
	vector<int> r;
	vector<int> odd, even;
	for(map<int,int>::iterator it = f.begin(); it != f.end(); it++){
		if(it->second == 1){
			r.push_back(it->first);
			if(it->first % 2){
				odd.push_back(it->first);
			} else {
				even.push_back(it->first);
			}
		}
	}
	PushRelabel s(300);
	for(int i = 0; i < 100; i++){
		s.add_edge(200,i,1);
		s.add_edge(i+100,201,1);
	}
	for(int i = 0; i < odd.size(); i++){
		for(int j = 0; j < even.size(); j++){
			int a = odd[i];
			int b = even[j];
			//cout << a << " " << b << endl;
			int p = abs(a-b);
			int ok = 1;
			if(p <= 1) ok = 0;
			for(int c = 2; c*c <= p; c++){
				if(p % c == 0){
					ok = 0;
					break;
				}
			}
			if(ok){
				s.add_edge(i,j+100,1);
			}
		}
	}
	int flow = (int)s.maxflow(200,201);
	int o1 = (int)odd.size() - flow;
	int e1 = (int)even.size() - flow;
	int ans = flow;
	ans += (o1/2)*2;
	ans += (e1/2)*2;
	o1 %= 2;
	e1 %= 2;
	if(o1 >= 1){
		ans += 3;
	}
	cout << ans << endl;
}

