int dfs1(int v) {
    was[v] = 1;
    for (int u: eds[v]) {
        if (p[u] == -1) {
            p[u] = v;
            p[v] = u;
            return 1;
        }
    }
    for (int u: eds[v]) {
        if (!was[p[u]] && dfs1(p[u])) {
            p[u] = v;
            p[v] = u;
            return 1;
        }
    }
