}

vector<int> v0;
vector<int> v1;

int main() {
    pr[0] = pr[1] = 1;
    for (int i = 2; i * i < MX; ++i) {
        if (pr[i])
            continue;
        for (int j = i * i; j < MX; j += i)
            pr[j] = 1;
    }
