        if (ss.count(x))
            ss.erase(x);
        else
            ss.insert(x);
        ss.insert(x + 1);
    }
    for (int i: ss) {
        if (i % 2 == 0)
            v0.push_back(i);
        else
            v1.push_back(i);
    }
    n = v0.size();
    m = v1.size();
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < m; ++j) {
            if (!pr[abs(v0[i] - v1[j])])
                eds[i].push_back(j + n);
        }
    fill(p, p + n + m, -1);
