    for (int i = 0; i < n; ++i) {
        memset(was, 0, sizeof(was));
        if (dfs1(i))
            ++cnt;
    }
    int ans = cnt;
    n -= cnt;
    m -= cnt;
    ans += 2 * (n / 2 + m / 2);
    if (n % 2 == 1)
        ans += 3;
    cout << ans << "\n";
