        if(i!=1 && a[i]>a[i-1]+1)pos[++tot]=a[i-1]+1;
    }
    pos[++tot]=a[n]+1;
    REP(i,1,tot)if(pos[i]%2)++cnt;
    REP(i,1,tot)if(pos[i]%2==0){
        REP(j,1,tot)if(pos[j]%2==1 && pd(abs(pos[j]-pos[i])))
            add(i,j);
    }
    REP(i,1,tot)if(pos[i]%2==0){
        memset(vis,0,sizeof(vis));
        ans+=Hungary(i);
    }
