        {
            if (odd.size() == 0 || odd.back() != tmp)
            {
                odd.push_back(tmp);
            }
            else
            {
                odd.pop_back();
            }
            even.push_back(tmp+1);
        }
        else
        {
            if (even.size() == 0 || even.back() != tmp)
            {
                even.push_back(tmp);
            }
            else
            {
                even.pop_back();
            }
            odd.push_back(tmp+1);
        }
    }

    find_prime(tmp);

    int N = odd.size(), M = even.size();
    Maxflow<int> flow(1+N+M+1);
    for (int i=1; i<=N; i++) flow.add_edge(0, i, 1);
    for (int i=1; i<=M; i++) flow.add_edge(N+i, 1+N+M, 1);
    for (int i=1; i<=N; i++)
    {
        for (int j=1; j<=M; j++)
        {
            if (binary_search(prime_Lst.begin(), prime_Lst.end(), abs(odd[i-1] - even[j-1])))
