struct Ford_Fulkerson {

	struct f_edge {
		ll to;
		ll cap;
		ll fl;
		ll rev;//
	};


	ll N;
	ll s, t;
	vector<vector<edge>> g;//flow
	vector<vector<f_edge>> flow;
	ll maxflow;

	vector<bool> see;
	ll dfs(ll v, ll x) {
		see[v] = 1;

		if (v == t) { return x; }

		rep(i, flow[v].size()) {
			const auto e = flow[v][i];

			if (see[e.to] == 1) { continue; }
			if (e.cap == e.fl) { continue; }

			ll y = min(x, e.cap - e.fl);

			y = dfs(e.to, y);
			if (y == 0) { continue; }

			flow[v][i].fl += y;
			flow[e.to][e.rev].fl -= y;
			return y;
		}

		return 0;
	}

	Ford_Fulkerson(ll N0, vector<vector<edge>> g0, ll s0, ll t0) {
		N = N0;
		g = g0;
		s = s0;
		t = t0;

		flow.resize(N + 1);
		repn(v, N) {
			for (edge e : g[v]) {
				ll w = e.to;
				flow[v].push_back({ w,e.d,0,flow[w].size() });
				flow[w].push_back({ v,0,0,flow[v].size() - 1 });
			}
		}

		maxflow = 0;

		if (s == t) { maxflow = INF; return; }

		while (1) {
			see.assign(N + 1, 0);
			ll f = dfs(s, INF);
			if (f == 0) { break; }
			maxflow += f;
		}

		maxflow = min(INF, maxflow);

		return;

	}
};

bool odprime(ll x) {
	if (x <= 2) { return 0; }
	for (ll i = 2; i * i <= x; i++) {
		if (x % i == 0) { return 0; }
	}

	return 1;
}

int main() {

	ll N;
	cin >> N;
	vll x(N + 1);
	repn(i, N) cin >> x[i];

	ll M = 20000000;
	vector<bool> omote(M + 1, 1);
	repn(i, N) { omote[x[i]] = 0; }

	vector<ll> a(1, 0);
	rep(i, M) { if (omote[i] != omote[i + 1]) { a.push_back(i); } }
	ll K = a.size() - 1;

	vector<vector<edge>> g(K + 3);
	ll ev = 0;
	ll od = 0;

	repn(i, K) {
		if (a[i] % 2 == 1) { g[K + 1].push_back({ 0,K + 1,i,1 }); od++; }
		else { g[i].push_back({ 0,i,K + 2,1 }); ev++; }
	}

	repn(i, K)repn(j, K) {
		if (a[i] % 2 == 1 && a[j] % 2 == 0 && odprime(abs(a[i] - a[j]))) {
			g[i].push_back({0,i,j,1});
		}
	}

	Ford_Fulkerson fd(K+2, g, K + 1, K + 2);

	ll ans = fd.maxflow;
	od -= ans;
	ev -= ans;
	ans += od / 2 * 2;
	ans += ev / 2 * 2;
	if (od % 2 == 1) { ans += 3; }

	cout << ans << endl;




}

