int n, a[N], my[N], ans;
bool b[N * 200];
vector<int> V[2], G[N];

bool find(int x) {
	b[x] = 1;
	for (int i = 0; i < G[x].size(); i++) {
		int u = G[x][i];
		if (my[u] == -1) {
			my[u] = x;
			return 1;
		} else {
			if (!b[my[u]] && find(my[u])) {
				my[u] = x;
				return 1;
			}
		}
	}
	return 0;
}

bool check(int x) {
	if (x == 2) return 0;
	for (int i = 2; i * i <= x; i++) {
		if (x % i == 0) return 0;
	}
	return 1;
}

