	}
	memset(my, -1, sizeof(my));
	for (int i = 0; i < V[0].size(); i++) {
		for (int j = 0; j < V[1].size(); j++) {
			if (check(abs(V[0][i] - V[1][j]))) 
				G[i].push_back(j);
		}
	}
	for (int i = 0; i < V[0].size(); i++) {
		for (int j = 0; j < V[0].size(); j++) b[j] = 0;
		if (find(i)) ans++;
	}
	cout << ans + (V[0].size() - ans) / 2 + (V[1].size() - ans) / 2 + V[0].size() % 2 * 3;
}

