namespace Network
{
	struct edge{
		int s,t,cap,next;
	}e[100010];
	int head[510],cnt;
	void addedge(int s,int t,int cap)
	{
		e[cnt].s=s;e[cnt].t=t;e[cnt].cap=cap;e[cnt].next=head[s];head[s]=cnt++;
		e[cnt].s=t;e[cnt].t=s;e[cnt].cap=0;e[cnt].next=head[t];head[t]=cnt++;
	}
	queue<int> q;
	int s,t;
	int dis[510]; 
	bool bfs()
	{
		q.push(s);
		memset(dis,0x3f,sizeof(dis));
		dis[s]=0;
		while (!q.empty())
		{
			int tmp=q.front();
			q.pop();
			for (int i=head[tmp];i!=-1;i=e[i].next)
				if (e[i].cap && dis[e[i].t]==INF)
				{
					dis[e[i].t]=dis[tmp]+1;
					q.push(e[i].t);
				}
		}
		return dis[t]!=INF;
	}
	int find(int x,int low)
	{
	    if (x==t)
	        return low;
	    int used=0;
	    for (int i=head[x];i!=-1;i=e[i].next)
	        if (e[i].cap>0 && dis[e[i].t]==dis[x]+1)
	        {
	            int flow=find(e[i].t,min(low-used,e[i].cap));
                e[i].cap-=flow;
                e[i^1].cap+=flow;
                used+=flow;
                if (used==low)
                    return low;
	        }
	    if (!used)
	        dis[x]=-1;
	    return used;
	}
	int dinic()
	{
		memset(head,0xff,sizeof(head));
		cnt=0;
		s=0,t=odd.size()+even.size()+1;
		for (int i=0;i<odd.size();i++)
			addedge(s,i+1,1);
		for (int i=0;i<even.size();i++)
			addedge(odd.size()+i+1,t,1);
		for (int i=0;i<odd.size();i++)
			for (int j=0;j<even.size();j++)
				if (!vis[abs(odd[i]-even[j])])
					addedge(i+1,odd.size()+j+1,1);
	    int ans=0;
	    while (bfs())
	        ans+=find(s,INF);
	    return ans;
	}
}
void ins(int x)
{
	if (x&1)
		odd.push_back(x);
	else
		even.push_back(x);
}
void pre()
{
	vis[1]=1; 
    for (int i=2;i<=10000000;i++)
    {
        if (!vis[i])
            prime[++p_cnt]=i;
        for (int j=1;j<=p_cnt&&i*prime[j]<=10000000;j++)
        {
            vis[i*prime[j]]=1;
            if (i%prime[j]==0)
                break;
        }
    }
}
