struct edge{
    int to;
    ll cap;
    int rev; //index
};

const int MAX_V = 1e3;
vector<edge> es[MAX_V];
vector<bool> used(MAX_V, false);

//fromtocap
void add_edge(int from, int to, ll cap){
    edge e1 = {to, cap, sz(es[to])}, e2 = {from, 0, sz(es[from])};
    es[from].eb(e1), es[to].eb(e2);
}

//dfs
ll dfs(int now, int goal, ll flow){
    if(now == goal) return flow;
    used[now] = true;
    for(auto &e: es[now]){
        if(!used[e.to] && e.cap > 0){
            ll f = dfs(e.to, goal, min(flow, e.cap));
            if(f > 0){
                e.cap -= f;
                es[e.to][e.rev].cap += f;
                return f;
            }
        }
    }
    return 0;
}

ll max_flow(int s, int t){
    ll flow = 0;
    while(true){
        fill(all(used), false);
        ll f = dfs(s, t, INF);
        if(f == 0) break;
        flow += f;
    }
    return flow;
}

