    }
    vector<int> od, ev;
    rep(i, MAX+1){
        if(a[i]^a[i+1]){
            if(i&1) od.pb(i);
            else ev.pb(i);
        } 
    }
    int n = sz(od), m = sz(ev), s = n+m, t = n+m+1;
    rep(i, n) add_edge(s, i, 1);
    rep(i, m) add_edge(n+i, t, 1);
    rep(i, n) rep(j, m){
        int d = abs(od[i]-ev[j]);
        bool p = (d != 1);
        for(int k = 2; k*k <= d; k++){
            if(d%k == 0) p = false;
        }
        if(p) add_edge(i, n+j, 1);
    }
    int f = max_flow(s, t);
    cout << n+m-f+(n-f)%2 << endl;
}

