void init() {
	nprm[1] = true;
	for(int i=2;i<=MAXN;i++) {
		if( !nprm[i] ) prm[++pcnt] = i;
		for(int j=1;prm[j]<=MAXN/i;j++) {
			nprm[i*prm[j]] = true;
			if( i % prm[j] == 0 ) break;
		}
	}
}

int a[MAXN + 5], b[2][MAXM + 5], cnt[2];

int G[MAXM + 5][MAXM + 5], lnk[MAXM + 5];
bool vis[MAXM + 5];
bool dfs(int x) {
	for(int i=1;i<=cnt[1];i++) {
		if( !vis[i] && G[x][i] ) {
			vis[i] = true;
			if( !lnk[i] || dfs(lnk[i]) ) {
				lnk[i] = x;
				return true;
			}
		}
	}
	return false;
}

