		for(int j=1;j<=cnt[1];j++)
			G[i][j] = (!nprm[abs(b[0][i] - b[1][j])]);
	for(int i=1;i<=cnt[0];i++) {
		for(int j=1;j<=cnt[1];j++) vis[j] = false;
		if( dfs(i) ) ans++;
	}
	ans += (cnt[0]/2*2 - ans) + (cnt[1]/2*2 - ans);
	if( cnt[0] & 1 ) ans += 3;
	printf("%d\n", ans);
}

