const int maxn = 205;
const int maxm = 1e7+5;
int n, m;
int a[maxn], b[maxm], x[maxm], vis1[maxn], vis2[maxn];
int is_prime[maxm], prime[maxm], cnt;
vector<int> to[maxn];
int X[maxn], Y[maxn], cnt1, cnt2;
void pre() {
    scanf("%d", &n);
    for(int i = 1; i <= n; i++) {
	scanf("%d", &a[i]);
	m = std::max(m, a[i]);
	x[a[i]] = 1;
    }
    m++;
    for(int i = 1; i <= m; i++) {
	b[i] = x[i] ^ x[i-1];
	if(b[i] == 1) {
	    if(i & 1) X[++cnt1] = i;
	    else Y[++cnt2] = i;
	}
    }
} 
void add_edge(int u, int v) {
    to[u].push_back(v);
}
void add(int u, int v) {
    add_edge(u, v);
    add_edge(v, u);
}
void build_graph() {
    for(int i = 1; i <= cnt1; i++) 
       for(int j = 1; j <= cnt2; j++) 
	    if(is_prime[abs(X[i] - Y[j])]) add(i, j + n); 
}
void shake(int n) {
    for(int i = 2; i <= n; i++) {
	if(!is_prime[i]) prime[++cnt] = i;
	for(int j = 1; j <= cnt && i * prime[j] <= n; j++) {
	    is_prime[i * prime[j]] = 1;
	    if((i % prime[j]) == 0) break;
	}
    }
}
bool dfs(int x) {
    for(int i = 0; i < to[x].size(); i++) {
	int v = to[x][i];
	if(!vis2[v]) {
	    vis2[v] = 1;
	    if(vis1[v] == 0 || dfs(vis1[v])) {
		vis1[x] = v;
		vis1[v] = x;
		return true;
	    }
	}
    }
    return false;
}
int bi() {
    int ans = 0;
    memset(vis1, -1, sizeof(vis1));
    for(int i = 1; i <= cnt1; i++) {
	if(vis1[i] == -1) {
	    memset(vis2, 0, sizeof(vis2));
	    if(dfs(i)) ans++;
	}
    }
    return ans;
}
int solve() {
    shake(m + 1);
    build_graph();
    return bi(); 
}
