#define INF	1e9
template <typename T> void read(T &x) {
	x = 0; int f = 1;
	char c = getchar();
	for (; !isdigit(c); c = getchar()) if (c == '-') f = -f;
	for (; isdigit(c); c = getchar()) x = x * 10 + c - '0';
	x *= f;
}
struct edge {int dest, flow, cost; unsigned home; };
int s, t, flow, cost, dist[MAXP];
vector <edge> a[MAXP];
int n, cnt, value[MAXP], path[MAXP];
unsigned home[MAXP];
int tot, f[MAXV], prime[MAXV];
bool isprime[MAXV], mark[MAXV];
void flow_path() {
	int ans = INF, p = t;
	while (p != s) {
		ans = min(ans, a[path[p]][home[p]].flow);
		p = path[p];
	}
	flow += ans, cost += ans * dist[t];
	p = t;
	while (p != s) {
		a[path[p]][home[p]].flow -= ans;
		a[p][a[path[p]][home[p]].home].flow += ans;
		p = path[p];
	}
}
bool spfa() {
	static int q[MAXQ];
	static bool inq[MAXP];
	for (int i = 1; i <= t; i++)
		dist[i] = INF;
	int l = 0, r = 0;
	dist[s] = 0; inq[s] = true; q[0] = s;
	while (l <= r) {
		int tmp = q[l++];
		inq[tmp] = false;
		for (unsigned i = 0; i < a[tmp].size(); i++)
			if (a[tmp][i].flow && dist[tmp] + a[tmp][i].cost < dist[a[tmp][i].dest]) {
				dist[a[tmp][i].dest] = dist[tmp] + a[tmp][i].cost;
				path[a[tmp][i].dest] = tmp;
				home[a[tmp][i].dest] = i;
				if (!inq[a[tmp][i].dest]) {
					inq[a[tmp][i].dest] = true;
					q[++r] = a[tmp][i].dest;
				}
			}
	}
	return dist[t] != INF;
}
void addedge(int s, int t, int f, int c) {
	a[s].push_back((edge) {t, f, c, a[t].size()});
	a[t].push_back((edge) {s, 0, -c, a[s].size() - 1});
}
int main() {
	read(n);
	for (int i = 1; i <= n; i++) {
		int x; read(x);
		mark[x] ^= true;
		mark[x + 1] ^= true;
	}
	for (int i = 1; i < MAXV; i++)
		if (mark[i]) value[++cnt] = i;
	for (int i = 2; i < MAXV; i++) {
		if (f[i] == i) {
			f[i] = i;
			prime[++tot] = i;
			isprime[i] = true;
		}
		for (int j = 1; j <= tot && prime[j] <= f[i]; j++) {
			int tmp = prime[j] * i;
			if (tmp >= MAXV) break;
			f[tmp] = prime[j];
		}
	}
	s = 0, t = cnt * 2 + 1;
	for (int i = 1; i <= cnt; i++) {
		addedge(s, i, 1, 0);
		addedge(i + cnt, t, 1, 0);
	}
	for (int i = 1; i <= cnt; i++)
	for (int j = i + 1; j <= cnt; j++) {
		int tmp = value[j] - value[i];
		if (tmp % 2 == 0) {
			addedge(i, j + cnt, 1, 2);
			addedge(j, i + cnt, 1, 2);
		} else if (isprime[tmp]) {
			addedge(i, j + cnt, 1, 1);
			addedge(j, i + cnt, 1, 1);
		} else {
			addedge(i, j + cnt, 1, 3);
			addedge(j, i + cnt, 1, 3);
		}
	}
	flow = 0, cost = 0;
	while (spfa())
		flow_path();
	cout << cost / 2 << endl;
	return 0;
}

