        for (int i=h[o];~i;i=e[i].nxt)
        if (!vis[e[i].to] && e[i].capa>e[i].flow)
        {
            vis[e[i].to]=1; q.push(e[i].to);
            d[e[i].to]=d[o]+1;
        }
    }
    return vis[t];
}

