map<int, int> mp;

bool isprime(int x){
	if(x == 1) return 0;
	for(int i=2; i*i<=x; i++){
		if(x % i == 0) return 0;
	}
	return 1;
}

int main(){
	cin >> n;
	for(int i=0; i<n; i++){
		int x; cin >> x;
		mp[x] ^= 1;
		mp[x + 1] ^= 1;
	}
	vector<int> l, r;
	for(auto &i : mp){
		if(i.second){
			if(i.first & 1) l.push_back(i.first);
			else r.push_back(i.first);
		}
	}
	for(int i=0; i<l.size(); i++){
		for(int j=0; j<r.size(); j++){
			if(isprime(abs(r[j] - l[i]))){
				bpm.add_edge(i, j);
			}
		}
	}
	int m = bpm.match(l.size());
	int ans = 1e8;
	for(int i=0; i<=m; i++){
		int ll = l.size() - i;
		int rl = r.size() - i;
		ans = min(ans, i + (ll / 2 + rl / 2) * 2 + (ll % 2) * 3);
	}
	cout << ans << endl;
}

