IL void init(int n) {
  np[1] = 1;
  For(i, 2, n) {
    if (!np[i]) {
      pri[++ p_cnt] = i;
    }
    for (int j = 1; j <= p_cnt && pri[j] * i <= n; ++ j) {
      np[i * pri[j]] = 1;
      if (i % pri[j] == 0) {
