  Edge() {}
  Edge(int _f, int _t, int _c) : from(_f), to(_t), resi(_c) {}
} edges[MAXM * 2];

std::vector<int> adj[MAXN];
int cnt;

IL void addEdge(int u, int v, int c) {
  edges[cnt] = Edge(u, v, c), adj[u].push_back(cnt);
  ++ cnt;
  edges[cnt] = Edge(v, u, 0), adj[v].push_back(cnt);
  ++ cnt;
}

int dist[MAXN], cur[MAXN];

IL bool BFS() {
  static int que[MAXN], vis[MAXN], num;
  ++ num;
  int l = 1, r = 0;
  que[++ r] = s, dist[s] = 0, vis[s] = num;
  while (l <= r) {
    int u = que[l ++];
    FOR(k, 0, SZ(adj[u])) {
      Edge &e = edges[adj[u][k]];
      if (vis[e.to] != num && e.resi)
        que[++ r] = e.to, dist[e.to] = dist[u] + 1, vis[e.to] = num;
    }
  }
  return vis[t] == num;
}

int DFS(int u, int x) {
  if (!x || u == t) {
    return x;
  }
  int flow = 0, f;
  for (int &k = cur[u]; k < SZ(adj[u]); ++ k) {
    Edge &e = edges[adj[u][k]];
    if (dist[e.to] == dist[u] + 1 && (f = DFS(e.to, mymin(x, e.resi)))) {
      edges[adj[u][k]].resi -= f, edges[adj[u][k] ^ 1].resi += f;
      flow += f, x -= f;
      if (!x)
        break;
      }
  }
  return flow;
}

IL int dinic() {
  int flow = 0;
  while (BFS()) {
    memset(cur, 0, sizeof cur);
    flow += DFS(s, INF);
  }
  return flow;
}

IL int solve() {
  int x = SZ(dpos[0]), y = SZ(dpos[1]);
  s = x + y + 1, t = x + y + 2;
  FOR(i, 0, x) {
    FOR(j, 0, y) {
      if (!np[myabs(dpos[0][i] - dpos[1][j])]) {
        addEdge(i + 1, j + 1 + x, 1);
      }
    }
  }
  For(i, 1, x) {
    addEdge(s, i, 1);
  }
  For(i, 1, y) {
    addEdge(i + x, t, 1);
  }
  return dinic();
}
}

