const int maxn = 100 + 15;
const int Maxsz = 2e7;
const int inf = 1e9 + 7;

int Dp[maxn][maxn] , n , x[maxn] , Prime[Maxsz + 1] , Primelen , Vis[Maxsz + 1];

void Init(){
	for(int i = 2 ; i <= Maxsz ; ++ i){
		if( !Vis[i] )
			Prime[Primelen ++] = i;
		for(int j = 0 ; j < Primelen && Prime[j] * i <= Maxsz ; ++ j){
			Vis[i * Prime[j]] = 1;
			if( i % Prime[j] == 0 ) break;
		}
	}
}

int Dfs( int l , int r ){
	if( ~Dp[l][r] ) return Dp[l][r];
	int & ans = Dp[l][r] = inf;
	if( l > r ) return ans = 0;
	if( l == r ) return ans = 3;
	int ret = 0;
	for(int i = l + 1 ; i <= r ; ++ i){
		if( x[i] - x[i - 1] - 1 == 0 ) ret += 0;
		else if( x[i] - x[i - 1] - 1 == 1 ) ret += 3;
		else if( x[i] - x[i - 1] - 1 == 2 ) ret += 2;
		else if( Vis[ x[i] - x[i - 1] - 1 ] == 0 ) ret += 1;
		else ret += 3;
	}
	int length = x[r] - x[l] + 1;
	if( length == 2 ) ret += 2;
	else if( Vis[length] == 0 ) ret += 1;
	else if( length & 1 ) ret += 3;
	else ret += 2;
	for(int i = l ; i <= r ; ++ i)
		for(int j = i ; j <= r ; ++ j)
			ans = min( ans , Dfs( l , i - 1 ) + Dfs( i , j ) + Dfs( j + 1 , r ) );
	return ans = min( ans , ret );
}

