  for (int i = 0; i < n; i++) {
    int a;
    scanf("%d", &a);
    cards[a] = true;
  }

  vector<int> flips;
  int odd = 0, even = 0;
  for (int i = 1; i <= 10000001; i++) {
    if (cards[i - 1] != cards[i]) {
      flips.push_back(i);
      if (i & 1) odd++;
      else even++;
    }
  }

  adjlist.resize(2 * flips.size());
  for (int i = 0; i < (int)flips.size(); i++) {
    if (flips[i] & 1) {
      for (int j = 0; j < (int)flips.size(); j++) {
        if (!(flips[j] & 1)) {
          int diff = abs(flips[i] - flips[j]);
          if (isPrime[diff]) {
            adjlist[i].push_back(flips.size() + j);
          }
        }
      }
    }
  }
  int m = kuhn(flips.size());
  int res = m + ((even - m) / 2 + (odd - m) / 2) * 2 + (even - m) % 2 * 3;
  printf("%d\n", res);
  return 0;
}

