	{
		if(!flag[i]) prime[++cnt] = i;
		for(int j = 1; j <= cnt && i * prime[j] < maxn; ++j)
		{
			flag[i * prime[j]] = 1;
			if(i % prime[j] == 0)
				break;
		}
	}
}
bool dfs(int now)
{
	for(auto v : E[now])
		if(vis[v] != cur)
		{
			vis[v] = cur;
			if(match[v] == -1 || dfs(match[v]))
			{
				match[v] = now;
				return true;
			}
		}
	return false;
}
