int solve(int n, VI &x) {
    struct edge { int i, j, len; };
    vector <edge> edges;

    FOR(i,0,n-1) FOR(j,0,n-1) if (i != j) {
        int diff = abs(x[i] - x[j]);

        int cost;
        if (diff % 2 == 0) {
            cost = 2;
        } else if (isPrime(diff)) {
            cost = 1;
        } else {
            cost = 3;
        }

        edges.pb({i, j, cost});
    }

    sort(edges.begin(), edges.end(), [](const edge &a, const edge &b) {
        return a.len < b.len;
    });

    set <int> left;
    FOR(i,0,n-1) left.insert(i);

    int ans = 0;
    for (auto &e : edges) if (left.count(e.i) && left.count(e.j)) {
        left.erase(e.i);
        left.erase(e.j);
        ans += e.len;
    }

    return ans;
}

