namespace G
{
	const int Max_V(205);
	const int Max_E((205 + 105 * 105) * 2);
	const int INF(0X3F3F3F3F);
	int V, S, T;
	int Head[Max_V], Total, To[Max_E], Next[Max_E], Cap[Max_E];
	inline void Add_Edge(const int &tot, const int &s, const int &t, const int &c)
	{
		To[tot] = t, Next[tot] = Head[s], Head[s] = tot, Cap[tot] = c;
	}
	inline void Add_Link(const int &s, const int &t, const int &c)
	{
		Total += 2, Add_Edge(Total, s, t, c), Add_Edge(Total ^ 1, t, s, 0);
	}
	int Cur[Max_V], Dist[Max_V];
	int Q[Max_V];
	bool BFS()
	{
		int ft(1);
		memset(Dist, 0, sizeof(Dist)), Dist[S] = 1, Q[Q[0] = 1] = S;
		int u;
		while (ft <= Q[0])
		{
			u = Q[ft++];
			for (int i = Head[u], v;i;i = Next[i])
				if (Cap[i] && !Dist[v = To[i]])
				{
					Dist[v] = Dist[u] + 1, Q[++Q[0]] = v;
					if (v == T)
						return true;
				}
		}
		return false;
	}
	int DFS(const int &u, int a)
	{
		if (u == T || a == 0)
			return a;
		int Ans(0), f;
		for (int &i = Cur[u], v;i;i = Next[i])
			if (Dist[v = To[i]] == Dist[u] + 1 && (f = DFS(v, min(a, Cap[i]))) > 0)
			{
				Ans += f, Cap[i] -= f, Cap[i ^ 1] += f;
				if ((a -= f) == 0)
					break;
			}
		return Ans;
	}
	int Dinic()
	{
		int Ans(0);
		while (BFS())
		{
			for (int i = 1;i <= V;++i)
				Cur[i] = Head[i];
			Ans += DFS(S, INF);
		}
		return Ans;
	}
}

