const int Max_N(105);
int A[Max_N << 1], B[Max_N << 1];
set<int> S;

void init()
{
	int N, X;
	for (scanf("%d", &N);N--;)
	{
		scanf("%d", &X);
		if (S.count(X))
			S.erase(X);
		else
			S.insert(X);
		if (S.count(X + 1))
			S.erase(X + 1);
		else
			S.insert(X + 1);
	}
	for (set<int>::iterator it = S.begin();it != S.end();++it)
		if ((*it) & 1)
			A[++A[0]] = *it;
		else
			B[++B[0]] = *it;
}

void work()
{
	G::V = S.size() + 2, G::S = S.size() + 1, G::T = S.size() + 2;
	for (int i = 1;i <= A[0];++i)
		G::Add_Link(G::S, i, 1);
	for (int i = A[0] + 1;i <= A[0] + B[0];++i)
		G::Add_Link(i, G::T, 1);
	for (int i = 1;i <= A[0];++i)
		for (int j = 1;j <= B[0];++j)
			if (isprime(Abs(A[i] - B[j])))
				G::Add_Link(i, A[0] + j, 1);
	int Ans = G::Dinic();
	A[0] -= Ans, B[0] -= Ans;
	Ans += (A[0] / 2) * 2, Ans += (B[0] / 2) * 2;
	if (A[0] & 1)
		Ans += 3;
	printf("%d", Ans);
}

