	efo(i,v,u)
		if(B[i][2] && !vis[u] && dis[v]==dis[u]+B[i][3])
		{
			ll f=aug(u,min(flow,B[i][2]));
			if(f)
			{
				B[i][2]-=f,B[i^1][2]+=f;
				return f;
			}
		}
	return 0;
}
bool change()
{
	ll minh=INF;
	fo(u,S,T) if(vis[u])
		efo(i,u,v) if(!vis[v])
			if(B[i][2]) minh=min(minh,dis[v]+B[i][3]-dis[u]);
	if(minh==INF) return 0;
	fo(i,S,T) if(vis[i]) dis[i]+=minh,vis[i]=0;
	return 1;
}
