{
	#define able(a, b) (abs(whi[a] - whi[b]) > 2 && isprime(abs(whi[a] - whi[b])))
	for(int i = 1; i <= m; i++)
		if(i != p && i != pygo[p] && able(x, i))
		{
			if(!pygo[i])
			{
				pygo[i] = x, pygo[x] = i;
				used[i] = used[x] = true;
				return true;
			}
			else
			{
				int t = pygo[i];
				pygo[t] = 0, pygo[i] = x, pygo[x] = i;
				used[i] = used[x] = true, used[t] = false;
