int pr[1000005], ptot, vis[10000005];
void pret(){
	int n = 1e7;
	REP(i, 2, n){
		if(!vis[i]) pr[++ptot] = i;
		for(int j = 1; j <= ptot && (long long)pr[j] * i <= n; j ++){
			vis[i * pr[j]] = 1;
			if(i % pr[j] == 0) break;
		}
	}
	vis[1] = 1;
	vis[2] = 1;
}

int ask(int x){
	if(!vis[x]) return 1;
	if(!(x & 1)) return 2;
	return 3;
}

void calc_max_weight_match()
{
	for (int v = 1; v <= n; v++)
		mate[v] = 0;

	n_x = n;
	n_matches = 0;
	tot_weight = ori_ans;

	bel[0] = 0;
	for (int v = 1; v <= n; v++)
		bel[v] = v, bloch[v].clear();
	for (int v = 1; v <= n; v++)
		for (int u = 1; u <= n; u++)
			blofrom[v][u] = v == u ? v : 0;

	int w_max = 0;
	for (int v = 1; v <= n; v++)
		for (int u = 1; u <= n; u++)
			relax(w_max, mat[v][u].w);
	for (int v = 1; v <= n; v++)
		lab[v] = w_max;

	while (match())
		n_matches++;

	for (int v = 1; v <= n; v++)
		if (mate[v] && mate[v] < v)
			tot_weight -= mat[v][mate[v]].w;
}


