int bfs(int S,int T)
{
	for(int i=S;i<=T;i++)dis[i]=0;
	dis[S]=1;q[1]=S;int head=0,tail=1;
	while(head<tail)
	{
		int u=q[++head];
		for(int i=fst[u];i;i=nxt[i])
		{
			int v=e[i].v;
			if(e[i].w&&!dis[v])
			{
				dis[v]=dis[u]+1;
				q[++tail]=v;
			}
		}
	}
	return dis[T];
}
int dfs(int u,int T,int lim)
{
	if(!lim||u==T)return lim;
	int add=0,f;
	for(int &i=cur[u];i;i=nxt[i])
	{
		int v=e[i].v;
		if(dis[v]==dis[u]+1&&e[i].w&&(f=dfs(v,T,min(lim,e[i].w))))
		{
			add+=f,lim-=f;
			e[i].w-=f,e[i^1].w+=f;
		}
	}
	return add;
}
void dinic(int S,int T)
{
	ans=0;
	while(bfs(S,T))
	{
		for(int i=S;i<=T;i++)cur[i]=fst[i];
		ans+=dfs(S,T,INF);
	}
}
