void Prime (int n)
{
	vis[1] = 1;
	for (Int i = 2;i <= n;++ i)
	{
		if (!vis[i]) prime[++ tot] = i;
		for (Int j = 1;j <= tot && i * prime[j] <= n;++ j)
		{
			vis[i * prime[j]] = 1;
			if (i % prime[j] == 0) break;
		}
	}
}

bool check (int x){return !vis[x];}

bool rev[MAXN];

int read ()
{
	int x = 0;char c = getchar();int f = 1;
	while (c < '0' || c > '9'){if (c == '-') f = -f;c = getchar();}
	while (c >= '0' && c <= '9'){x = (x << 3) + (x << 1) + c - '0';c = getchar();}
	return x * f;
}

void write (int x)
{
	if (x < 0){x = -x;putchar ('-');}
	if (x > 9) write (x / 10);
	putchar (x % 10 + '0');
}

vector <int> s1,s2;

struct edge
{
	int v,nxt;
}e[MAXN << 1];

int top = 1;
int head[MAXN];

void Add_Edge (int u,int v)
{
	e[++ top] = edge {v,head[u]};
	head[u] = top;
}

int my[MAXN];
bool visit[MAXN];

int dfs (int u)
{
	for (Int i = head[u];i;i = e[i].nxt)
	{
		int v = e[i].v;
		if (visit[v]) continue;
		visit[v] = 1;
		if (my[v] == -1 || dfs (my[v]))
		{
			my[v] = u;
			return 1;
		}
	}
	return 0;
}

int Hungry ()
{
	int cnt = 0;
	memset (my,-1,sizeof (my));
	for (Int i = 0;i < s1.size();++ i)
	{
		memset (visit,0,sizeof (visit));
		cnt += dfs (i);
	}
	return cnt;
}

