const int M=10000005;
int spf[M+3];
void sieve()
{
    spf[1]=1;
    f(i,1,M)spf[i]=i;
    for(int i=4;i<=M;i=i+2)
    {
        spf[i]=2;
    }
    for(int i=3;i*i<=M;i=i+2)
    {
        if(spf[i]==i)
        {
           for(int j=i*i;j<=M;j=j+i)
           {
               if(spf[j]==j)
               {
                   spf[j]=i;
               }
           }
        }
    }
}
int memo[M];
int w_memo[M];
int yo(int );
int work(int x)
{
    int &ret=w_memo[x];
    if(ret!=(-1))return ret;
    int cost=inf;
    for(int i=3;i<x;i=i+2)
    {
        int tmp=yo(i)+yo(x-i);
        cost=min(cost,tmp);
    }
    ret=cost;
    return cost;
}
int yo(int val)
{   int &ret=memo[val];
    if(ret!=(-1))return ret;
    ret=0;
    if(val==1 || val==2)return ret=inf;
    if(val%2==0)
    {
        ret=work(val);
        return ret;
    }
    while(val!=1)
    {
        int p=spf[val],cnt=1;
        while(val%p==0)val=val/p,cnt=cnt*p;
        cnt=cnt/p;
        ret+=cnt;
    }
    return ret;
}
