struct BiMatch{
  Int n;
  vector<vector<Int> > G;
  vector<Int> match,used;

  BiMatch(){}
  BiMatch(Int sz):n(sz),G(sz),match(sz),used(sz){}

  void add_edge(Int u,Int v){
    G[u].push_back(v);
    G[v].push_back(u);
  }

  bool dfs(Int v){
    used[v]=true;
    for(Int i=0;i<(Int)G[v].size();i++){
      Int u=G[v][i],w=match[u];
      if(w<0||(!used[w]&&dfs(w))){
	match[v]=u;
	match[u]=v;
	return true;
      }
    }
    return false;
  }

  Int build(){
    Int res=0;
    fill(match.begin(),match.end(),-1);
    for(Int v=0;v<n;v++){
      if(match[v]<0){
	fill(used.begin(),used.end(),0);
	if(dfs(v)){
	  res++;
	}
      }
    }
    return res;
  }
};


