typedef long long ll;

#define N 10000050
#define INF 30000000
#define Min(a,b) (a<b?a:b)

int noprm[N];
int prm[N/5],pcnt;
void init(int lim)
{
	noprm[1]=1;
	for(int i=2;i<=lim;i++)
	{
		if(!noprm[i])
		{
			prm[++pcnt]=i;
		}
		for(int j=1;j<=pcnt && (ll)i*prm[j]<=lim;j++)
		{
			noprm[i*prm[j]]=1;
			if(i%prm[j]==0) break;
		}
	}
}

int a[105];
int tp[205],len[205];
int f[2][205][205];

inline int calc(int p)
{
	return (p&1)? (noprm[p]? 3: 1): 2;
}

int solve(int tp,int l,int r)
{
	if(f[tp][l][r]!=-1) return f[tp][l][r];
	if(l==r) return INF;
	int &res1=f[tp][l][r];
	int &res2=f[tp^1][l][r];
	res1=res2=INF;
	for(int i=l;i<=r;i++)
		for(int j=i;j<=r;j++)
		{
			if(i==l && j==r) continue;
			int tmp1= i>l? solve(tp,l,i-1): 0;
			int tmp2=solve(tp^1,i,j);
			int tmp3= j<r? solve(tp,j+1,r): 0;
			int tmp4=calc(len[j]-len[i-1]);
			res1=Min(res1,tmp1+tmp2+tmp3+tmp4);
			tp^=1;
			tmp1= i>l? solve(tp,l,i-1): 0;
			tmp2=solve(tp^1,i,j);
			tmp3= j<r? solve(tp,j+1,r): 0;
			res2=Min(res2,tmp1+tmp2+tmp3+tmp4);
			tp^=1;
		}
	int tmp=calc(len[r]-len[l-1]);
	res1=Min(res1,res2+tmp);
	res2=Min(res2,res1+tmp);
	res1=Min(res1,res2+tmp);
	res2=Min(res2,res1+tmp);
	return res1;
}

int main()
{
	init(10000000);
	int n;scanf("%d",&n);
	for(int i=1;i<=n;i++)
		scanf("%d",&a[i]);
	int scnt=0,start=1;
	if(a[1]>1)
	{
		++scnt;
		tp[scnt]=0;
		len[scnt]=a[1]-1;
	}

	for(int i=2;i<=n+1;i++)
		if(i==n+1)
		{
			++scnt;
			tp[scnt]=1,len[scnt]=a[i-1]-a[start]+1;
		}
		else if(a[i]!=a[i-1]+1)
		{
			++scnt;
			tp[scnt]=1,len[scnt]=a[i-1]-a[start]+1;
			++scnt;
			tp[scnt]=0,len[scnt]=a[i]-a[i-1]-1;
			start=i;
		}
	++scnt;
	tp[scnt]=0,len[scnt]=3;
	memset(f,-1,sizeof(f));
	for(int i=1;i<=scnt;i++)
		f[tp[i]][i][i]=0,f[tp[i]^1][i][i]=calc(len[i]);
	for(int i=1;i<=scnt;i++)
		len[i]+=len[i-1];
	int ans=solve(0,1,scnt);
	printf("%d\n",ans);
}

