int primes[RNG], totprimes;
bool composite[RNG];

void sieve(int n)
{
    composite[1] = true;
    for (int i = 2; i <= n; ++i) {
        if (!composite[i])
            primes[totprimes++] = i;
        for (int j = 0, x; (x = primes[j] * i) <= n; ++j) {
            composite[x] = true;
            if (i % primes[j] == 0)
                break;
        }
    }
}

vector<int> odd, even;
bool G[MAXN][MAXN];
int match[MAXN];
bool vis[MAXN];

bool augment(int u)
{
    for (int v = 0; v < SZ(even); ++v) {
        if (G[u][v] && !vis[v]) {
            vis[v] = true;
            if (!match[v] || augment(match[v])) {
                match[v] = u;
                return true;
            }
        }
    }
    return false;
}

void solve()
{
    sieve(X[N]);

    X[0] = -1;
    X[N + 1] = RNG;
    for (int i = 1; i <= N; ++i) {
        if (X[i - 1] + 1 < X[i])
            (X[i] & 1 ? odd : even).push_back(X[i]);
        if (X[i] + 1 < X[i + 1])
            ((X[i] + 1) & 1 ? odd : even).push_back(X[i] + 1);
    }

    for (int i = 0; i < SZ(odd); ++i) {
        for (int j = 0; j < SZ(even); ++j) {
            if (!composite[std::abs(odd[i] - even[j])])
                G[i][j] = true;
        }
    }
    int t = 0;
    for (int i = 0; i < SZ(odd); ++i) {
        memset(vis, 0, sizeof(vis));
        t += augment(i);
    }
    int ans = t + ((SZ(odd) - t) / 2 + (SZ(even) - t) / 2) * 2 + (SZ(odd) - t) % 2 * 3;

    printf("%d\n", ans);
}

